COMMAND.ACTION:
if test "${summary}" = ""; then

	python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_sonic_neigh_sonic_neigh_neigh_table script:arp_show.j2 family:IPv4 ip:${ip-addr} mac:${mac-addr} vrf:${vrf-name} summary:${summary}

else

	python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_sonic_neigh_sonic_neigh_neigh_table script:arp_summary_show.j2 family:IPv4 ip:${ip-addr} mac:${mac-addr} vrf:${vrf-name} summary:${summary}

fi

if test "${summary}" = ""; then

    if test "${iftype}" = "Ethernet"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_show.j2 intf:${iftype}${phy-if-name} summary:${summary}

    fi

    if test "${iftype}" = "Loopback"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_show.j2 intf:${iftype}${lo-id} summary:${summary}

    fi

    if test "${iftype}" = "Management"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_show.j2 intf:eth${mgmt-if-id} summary:${summary}

    fi

    if test "${iftype}" = "PortChannel"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_show.j2 intf:${iftype}${lag-id} summary:${summary}

    fi

    if test "${iftype}" = "Vlan"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_show.j2 intf:${iftype}${vlan-id} summary:${summary}

    fi

    if test "${iftype}" = "Vxlan"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_show.j2 intf:${iftype}${vxlan-if-name} summary:${summary}

    fi

else

    if test "${iftype}" = "Ethernet"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_summary_show.j2 intf:${iftype}${phy-if-name} summary:${summary}

    fi

    if test "${iftype}" = "Loopback"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_summary_show.j2 intf:${iftype}${lo-id} summary:${summary}

    fi

    if test "${iftype}" = "Management"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_summary_show.j2 intf:eth${mgmt-if-id} summary:${summary}

    fi

    if test "${iftype}" = "PortChannel"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_summary_show.j2 intf:${iftype}${lag-id} summary:${summary}

    fi

    if test "${iftype}" = "Vlan"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_summary_show.j2 intf:${iftype}${vlan-id} summary:${summary}

    fi

    if test "${iftype}" = "Vxlan"; then

        python $SONIC_CLI_ROOT/sonic-cli-neighbors.py get_openconfig_if_ip_interfaces_interface_subinterfaces_subinterface_ipv4_neighbors script:arp_summary_show.j2 intf:${iftype}${vxlan-if-name} summary:${summary}

    fi

fi

if test "${force}" = ""; then
    python $SONIC_CLI_ROOT/sonic-cli-neighbors.py rpc_sonic_clear_neighbors family:IPv4 force:false intf:${if-type}${if-id}

else
    python $SONIC_CLI_ROOT/sonic-cli-neighbors.py rpc_sonic_clear_neighbors family:IPv4 force:true intf:${if-type}${if-id}

fi

if test "${force}" = ""; then
    python $SONIC_CLI_ROOT/sonic-cli-neighbors.py rpc_sonic_clear_neighbors family:IPv4 force:false ip:${ip-addr}

else
    python $SONIC_CLI_ROOT/sonic-cli-neighbors.py rpc_sonic_clear_neighbors family:IPv4 force:true ip:${ip-addr}

fi
